        text: 'Doctor Appnt',
        day: 'feb friday ',
        reminder: 'true',

    },
    {
        id: 2,
        text: 'Doctor Appnt2',
        day: 'feb sunday ',
        reminder: 'true',

    },
    {
        id: 3,
        text: 'Doctor Abkono',
        day: 'feb friday ',
        reminder: 'false',

    }
])
//Delete Task
